import React, { useState, useEffect } from 'react';
import './alarm.css'; // Import the updated CSS file
import BatteryIcon from './battery.png'; // Import the battery icon
import ClockIcon from './clock.png'; // Import the clock icon

export function Alarm({ pageTitle }) {
  const [alarmData, setAlarmData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.73.150:5000'; // Update to your Flask server URL

  useEffect(() => {
    const eventSource = new EventSource(`${apiBaseUrl}/stream_alarm`);

    eventSource.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        setAlarmData(data);  // Update alarm data in real-time
      } catch (err) {
        setError('Failed to parse server response');
      }
    };

    eventSource.onerror = () => {
      setError('Error connecting to server');
      eventSource.close();
    };

    return () => {
      eventSource.close(); // Close the connection when the component is unmounted
    };
  }, []);

  const renderAlarmCard = (property, value) => {
    const formatTime = (time) => (time ? `${time}'` : 'N/A');

    return (
      <div className="alarm-card">
        <div className="alarm-card-content">
          {(property === 'On/Off' || property === 'Motion Detection') && (
            <div className={`alarm-status-circle ${value === 'on' ? 'on' : 'off'}`}></div>
          )}
          {(property === 'Battery' && <img src={BatteryIcon} alt="Battery Icon" className="battery-icon" />) ||
            (property === 'Timer' && <img src={ClockIcon} alt="Clock Icon" className="clock-icon" />)}
          <div className="alarm-info">
            <div className="alarm-property">{property}</div>
            <div className="alarm-value">
              {property === 'Battery' ? value + '%' : property === 'Timer' ? formatTime(value) : value}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {alarmData ? (
        <div className="alarm-cards-container">
          {renderAlarmCard('On/Off', alarmData.on_off || 'N/A')}
          {renderAlarmCard('Battery', alarmData.battery || 'N/A')}
          {renderAlarmCard('Timer', alarmData.timer || '0')}
          {renderAlarmCard('Motion Detection', alarmData.motion_detection || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}

export default Alarm;
