import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import the updated CSS file

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.2.12:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/text`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Fetched full data:', data); // Debugging statement
        setFullData(data); // Save the full data
        const filtered = data.data.filter(item => item.device === 'alarm'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();

    // Optionally, set an interval to refresh the data
    const intervalId = setInterval(fetchData, 5000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  // Reverse data for bottom-to-top display
  const reversedFullData = fullData ? [...fullData.data].reverse() : [];
  const reversedFilteredData = filteredData ? [...filteredData].reverse() : [];

  // Extract properties for the first table
  const extractProperties = (data) => {
    if (data.length > 0) {
      // Assuming the first item contains the alarm properties
      const item = data[0];
      return [
        { property: 'on_off', value: item.on_off || 'N/A' },
        { property: 'battery', value: item.battery || 'N/A' },
        { property: 'timer', value: item.timer || 'N/A' },
        { property: 'motion_detection', value: item.motion_detection || 'N/A' }
      ];
    }
    return [];
  };

  const propertiesData = extractProperties(reversedFullData);

  return (
    <div className="text-center">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData && (
        <div className="tables-container">
          {/* Table for alarm properties */}
          <div className="table-container-wrapper">
            <h2>Alarm Properties</h2>
            <div className="table-container">
              <table className="table-container1">
                <thead>
                  <tr className="row_0">
                    <th>Property</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {propertiesData.map((item, index) => (
                    <tr key={index}>
                      <td>{item.property}</td>
                      <td>{item.value}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Table for filtered alarm data */}
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {reversedFilteredData.map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
      {!fullData && <p>Loading data...</p>}
    </div>
  );
}
