import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import the updated CSS file

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.152:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/alarm`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFullData(data); // Save the full data

        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'alarm'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  const renderAlarmCard = (property, value) => {
    const isOn = property === 'on_off' && value === 'off';
    const isActivated = property === 'Motion Detection' && value === 'activated';

    return (
      <div className="alarm-card">
        <div className={`alarm-status ${property === 'on_off' ? (isOn ? 'on' : 'off') : property === 'Motion Detection' ? (isActivated ? 'on' : 'off') : ''}`}></div>
        <div className="alarm-property">{property}</div>
        <div className="alarm-value">
          {property === 'Battery' && (
            <div className={`battery-status ${getBatteryClass(value)}`}>
              <span className="battery-icon">ðŸ”‹</span> {value}%
            </div>
          )}
          {property === 'Timer' ? formatTime(value) : value}
        </div>
      </div>
    );
  };

  const getBatteryClass = (level) => {
    if (level >= 40) return 'battery-green';
    if (level >= 10) return 'battery-orange';
    return 'battery-red';
  };

  const formatTime = (time) => {
    return time ? `${time}'` : 'N/A';
  };

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData ? (
        <div className="alarm-cards-container">
          {renderAlarmCard('on_off', fullData.on_off ? 'On' : 'Off')}
          {renderAlarmCard('Battery', fullData.battery || 'N/A')}
          {renderAlarmCard('Timer', fullData.timer || 'N/A')}
          {renderAlarmCard('Motion Detection', fullData.motion_detection || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{formatTime(item.time)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alarm;
