import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import the updated CSS file

export function Alarm({ pageTitle }) {
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.2.12:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchFilteredData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/text`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        const filtered = data.data.filter(item => item.device === 'alarm'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchFilteredData();

    // Optionally, set an interval to refresh the data
    const intervalId = setInterval(fetchFilteredData, 5000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="text-center">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {filteredData ? (
        <div className="tables-container">
          <table className="table-container2">
            <thead>
              <tr className="row_0">
                <th>Original Text</th>
                <th>NLP Text</th>
                <th>Device</th>
                <th>Flag</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {filteredData.map((item, index) => (
                <tr key={index}>
                  <td>{item.original_text}</td>
                  <td>{item.nlp_text}</td>
                  <td>{item.device}</td>
                  <td>{item.flag}</td>
                  <td>{item.time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}
