// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Alarm } from './Alarm'; // Import the Alarm component
import './main_page.css';

// Component for Home Page
function Home() {
  const [jsonData, setJsonData] = useState(null);
  const [lastModified, setLastModified] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {

        const response = await fetch('C:/Users/Thomas/Desktop/web(leitourgiko-palio, july 24)/web/flask-server/data.json');
        const newData = await response.json();
        const newLastModified = response.headers.get('Last-Modified');

        if (newLastModified !== lastModified) {
          setJsonData(newData);
          setLastModified(newLastModified);
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 2000);

    return () => clearInterval(intervalId);
  }, [lastModified]);

  const getNLPText = () => {
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      return jsonData.data[jsonData.data.length - 1].nlp_text;
    }
    return '';
  };

  const getTextColor = () => {
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      const flag = jsonData.data[jsonData.data.length - 1].flag;
      if (flag === 'correct') {
        return 'green';
      } else if (flag === 'incorrect') {
        return 'red';
      }
    }
    return '';
  };

  const renderTable = () => {
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      return (
        <table>
          <thead>
            <tr className="row_0">
              <th>Command</th>
              <th>Valid/No Valid</th>
              <th>Device</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {jsonData.data.map((item, index) => (
              <tr key={index}>
                <td>{item.nlp_text}</td>
                <td>{item.flag}</td>
                <td>{item.device}</td>
                <td>{item.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    } else {
      return <p>No data available.</p>;
    }
  };

  return (
    <div>
      <div className="header">
        <h2>Starting Page</h2>
        <hr className="line-under-header"></hr>
      </div>
      {jsonData ? (
        <>
          <p className="text-center">
            <span style={{ color: 'black' }}><h4>Last command that got triggered:</h4></span>
            <span style={{ color: getTextColor() }}>{getNLPText()}</span>
          </p>
          <div className="button-center">
            <Link to="/aircondition">
              <button className="button">
                <span>Air Condition</span>
              </button>
            </Link>
            <Link to="/alarm">
              <button className="button">
                <span>Alarm</span>
              </button>
            </Link>
          </div>
          <div style={{ marginTop: '50px' }}>
            {renderTable()}
          </div>
        </>
      ) : (
        <p className="text-center">Loading...</p>
      )}
    </div>
  );
}

// Component for About Page
function About() {
  return (
    <div>
      <h1>About Page</h1>
      <p>You are currently on the About page.</p>
      <nav>
        <Link to="/">Go to Home Page</Link>
      </nav>
    </div>
  );
}

// Main App Component with Routing
function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/aircondition" element={<AirCondition pageTitle="AirCondition Page" />} />
        <Route path="/alarm" element={<Alarm pageTitle="Alarm Page" />} />
      </Routes>
    </Router>
  );
}

export default App;
