import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Alarm } from './Alarm'; // Import your Alarm component

function Home() {
  return (
    <div>
      <h1>Home Page</h1>
      <p>You are currently on the Home page.</p>
      <nav>
        <Link to="/about">Go to About Page</Link> | 
        <Link to="/alarm">Go to Alarm Page</Link> {/* Add link to Alarm page */}
      </nav>
    </div>
  );
}

function About() {
  return (
    <div>
      <h1>About Page</h1>
      <p>You are currently on the About page.</p>
      <nav>
        <Link to="/">Go to Home Page</Link> | 
        <Link to="/alarm">Go to Alarm Page</Link> {/* Add link to Alarm page */}
      </nav>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/alarm" element={<Alarm pageTitle="Alarm Page" />} /> {/* Add route for Alarm */}
      </Routes>
    </Router>
  );
}

export default App;
