import React, { useState, useEffect } from 'react';
import './alarm.css'; // Import the updated CSS file
import BatteryIcon from './battery.png'; // Import the battery icon
import ClockIcon from './clock.png'; // Import the clock icon

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.7:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const alarmResponse = await fetch(`${apiBaseUrl}/alarm`);
        if (!alarmResponse.ok) {
          throw new Error(`HTTP error! Status: ${alarmResponse.status}`);
        }
        const alarmData = await alarmResponse.json();
        console.log('Fetched Alarm Data:', alarmData); // Debugging line
        setFullData(alarmData);

        const textResponse = await fetch(`${apiBaseUrl}/text`);
        if (!textResponse.ok) {
          throw new Error(`HTTP error! Status: ${textResponse.status}`);
        }
        const textData = await textResponse.json();
        const filtered = textData.data.filter(item => item.device === 'alarm');
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId); // Proper cleanup
  }, [apiBaseUrl]);

  const normalizeOnOffValue = (value) => {
    if (!value) return 'N/A';
    const normalized = value.toLowerCase();
    return normalized === 'on' || normalized === 'off'
      ? normalized.charAt(0).toUpperCase() + normalized.slice(1)
      : 'N/A';
  };

  const formatTime = (time) => {
    console.log('Timer Value:', time); // Debugging line
    // Handle cases where time is null, undefined, or negative
    if (time === null || time === undefined || time < 0) return 'N/A';
    if (time === 0) return `0'`;
    return `${time}'`;
  };

  const renderAlarmCard = (property, value) => {
    const normalizedOnOff = property === 'On/Off' ? normalizeOnOffValue(value) : '';
    const isOn = normalizedOnOff === 'On';
    const isActivated = property === 'Motion Detection' && value === 'activated';

    return (
      <div className="alarm-card" key={property}>
        <div className="alarm-card-content">
          {(property === 'On/Off' || property === 'Motion Detection') && (
            <div className={`alarm-status-circle ${isOn || isActivated ? 'on' : 'off'}`}></div>
          )}
          {property === 'Battery' && <img src={BatteryIcon} alt="Battery Icon" className="battery-icon" />}
          {property === 'Timer' && <img src={ClockIcon} alt="Clock Icon" className="clock-icon" />}
          <div className="alarm-info">
            <div className="alarm-property">{property}</div>
            <div className="alarm-value">
              {property === 'Battery' ? `${value || 'N/A'}%` : property === 'Timer' ? formatTime(value) : value}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData ? (
        <div className="alarm-cards-container">
          {console.log('Timer Value in Render:', fullData.timer)} {/* Debugging line */}
          {renderAlarmCard('On/Off', fullData.on_off || 'N/A')}
          {renderAlarmCard('Battery', fullData.battery || 'N/A')}
          {renderAlarmCard('Timer', fullData.timer || 'N/A')}
          {renderAlarmCard('Motion Detection', fullData.motion_detection || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alarm;
