import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import './Devices.css'; // Import CSS file

function Home() {
  return (
    <div>
      <h1>Home Page</h1>
      <p>You are currently on the Home page.</p>
      <nav>
        <Link to="/about">Go to About Page</Link>
        <br />
        <Link to="/alarm">Go to Alarm Page</Link>
      </nav>
    </div>
  );
}

function About() {
  return (
    <div>
      <h1>About Page</h1>
      <p>You are currently on the About page.</p>
      <nav>
        <Link to="/">Go to Home Page</Link>
        <br />
        <Link to="/alarm">Go to Alarm Page</Link>
      </nav>
    </div>
  );
}

function Alarm() {
  const [mainData, setMainData] = useState(null);
  const [additionalData, setAdditionalData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/web/web/flask-server/alarm.json');
        const newData = await response.json();
        setMainData(newData);
      } catch (error) {
        console.error('Error fetching main data:', error);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/web/web/flask-server/data.json');
        const allData = await response.json();
        const filteredData = allData.data.filter(item => item.device === 'alarm');
        setAdditionalData(filteredData);
      } catch (error) {
        console.error('Error fetching additional data:', error);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="Alarm text-center">
      {mainData && additionalData && (
        <div className="tables-container">
          <table className="table-container1">
            <thead>
              <tr>
                <th className="row_0">Property</th>
                <th className="row_0">Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(mainData).map(([key, value]) => (
                <tr key={key}>
                  <td>{key}</td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <table className="table-container2">
            <thead>
              <tr>
                <th>Original Text</th>
                <th>NLP Text</th>
                <th>Device</th>
                <th>Flag</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {additionalData.map((item, index) => (
                <tr key={index}>
                  <td>{item.original_text}</td>
                  <td>{item.nlp_text}</td>
                  <td>{item.device}</td>
                  <td>{item.flag}</td>
                  <td>{item.time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/alarm" element={<Alarm />} />
      </Routes>
    </Router>
  );
}

export default App;
