import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import CSS file

export function AirCondition({ pageTitle }) {
  const [mainData, setMainData] = useState(null);
  const [additionalData, setAdditionalData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.2.12:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchMainData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/air_condition`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Fetched main data:', data); // Debugging statement
        setMainData(data);
      } catch (error) {
        setError('Error fetching main data: ' + error.message);
      }
    };

    const fetchAdditionalData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/text`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const allData = await response.json();
        const filteredData = allData.data.filter(item => item.device === 'air condition');
        setAdditionalData(filteredData);
      } catch (error) {
        setError('Error fetching additional data: ' + error.message);
      }
    };

    fetchMainData();
    fetchAdditionalData();
    
    // Optionally, set an interval to refresh the data
    const intervalId = setInterval(() => {
      fetchMainData();
      fetchAdditionalData();
    }, 5000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="AirCondition text-center">
      {mainData && additionalData && (
        <div className="tables-container">
          <table className="table-container1">
            <thead>
              <tr>
                <th className="row_0">Property</th>
                <th className="row_0">Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(mainData).map(([key, value]) => (
                <tr key={key}>
                  <td>{key}</td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <table className="table-container2">
            <thead>
              <tr>
                <th>Original Text</th>
                <th>NLP Text</th>
                <th>Device</th>
                <th>Flag</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {additionalData.map((item, index) => (
                <tr key={index}>
                  <td>{item.original_text}</td>
                  <td>{item.nlp_text}</td>
                  <td>{item.device}</td>
                  <td>{item.flag}</td>
                  <td>{item.time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      {!mainData && <p>Loading data...</p>}
    </div>
  );
}
