import React, { useState, useEffect } from 'react';
import { AirCondition } from './AirCondition';
import { Alarm } from './Alarm';
import './main_page.css';

function Home() {
  const [jsonData, setJsonData] = useState(null);
  const [lastModified, setLastModified] = useState(null);
  const [page, setPage] = useState('pageOne');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://192.168.2.12:5000/text'); // Update to your Flask server URL
        if (response.ok) {
          const newData = await response.json();
          const newLastModified = response.headers.get('Last-Modified');
          console.log("Fetched data:", newData); // Log the fetched data
          
          if (newLastModified !== lastModified) {
            setJsonData(newData);
            setLastModified(newLastModified);
          }
        } else {
          console.error('Fetch error:', response.status, response.statusText); // Log fetch errors
        }
      } catch (error) {
        console.error('Error fetching data:', error); // Log other errors
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 2000);

    return () => clearInterval(intervalId);
  }, [lastModified]);

  const getNLPText = () => {
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      return jsonData.data[jsonData.data.length - 1].nlp_text;
    }
    return '';
  };

  const getTextColor = () => {
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      const flag = jsonData.data[jsonData.data.length - 1].flag;
      if (flag === 'correct') {
        return 'green';
      } else if (flag === 'incorrect') {
        return 'red';
      }
    }
    return 'black'; // Default color if flag is not present
  };

  const renderTable = () => {
    console.log('Rendering table with data:', jsonData); // Debugging log
    if (jsonData && jsonData.data && jsonData.data.length > 0) {
      return (
        <table>
          <thead>
            <tr className="row_0">
              <th>Command</th>
              <th>Valid/No Valid</th>
              <th>Device</th>
              <th>Time</th>
            </tr>
          </thead>
          <tbody>
            {jsonData.data.map((item, index) => (
              <tr key={index}>
                <td>{item.nlp_text}</td>
                <td>{item.flag}</td>
                <td>{item.device}</td>
                <td>{item.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    } else {
      return <p>No data available.</p>;
    }
  };

  const handlePageChange = (pageName) => {
    setPage(pageName);
  };

  // Debugging render
  console.log('Current Page:', page);
  console.log('JSON Data:', jsonData);

  return (
    <div>
      <div className="header">
        <h2>{page === 'pageOne' ? 'Starting Page' : page === 'pageTwo' ? 'AirCondition Page' : 'Alarm Page'}</h2>
        <hr className="line-under-header" />
      </div>
      {page === 'pageOne' ? (
        <div>
          {jsonData ? (
            <>
              <p className="text-center">
                <span style={{ color: 'black' }}><h4>Last command that got triggered:</h4></span>
                <span style={{ color: getTextColor() }}>{getNLPText()}</span>
              </p>
              <div className="button-center">
                <button className="button" onClick={() => handlePageChange('pageTwo')}>
                  <span>Air Condition</span>
                </button>
                <button className="button" onClick={() => handlePageChange('pageThree')}>
                  <span>Alarm</span>
                </button>
              </div>
              <div style={{ marginTop: '50px' }}>
                {renderTable()}
              </div>
            </>
          ) : (
            <p className="text-center">Loading...</p>
          )}
        </div>
      ) : page === 'pageTwo' ? (
        <div>
          <AirCondition pageTitle="AirCondition Page" />
          <div className="button-center">
            <button className="button" onClick={() => handlePageChange('pageOne')}>
              <span>Starting Page</span>
            </button>
          </div>
        </div>
      ) : (
        <div>
          <Alarm pageTitle="Alarm Page" />
          <div className="button-center">
            <button className="button" onClick={() => handlePageChange('pageOne')}>
              <span>Starting Page</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;
