import React, { useState, useEffect } from 'react';
import './alarm.css'; // Import the updated CSS file
import BatteryIcon from './battery.png'; // Import the battery icon
import ClockIcon from './clock.png'; // Import the clock icon

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);
  const [countdownTimer, setCountdownTimer] = useState(null); // New state to handle countdown

  const apiBaseUrl = 'http://192.168.1.7:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/alarm`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Fetched Alarm Data:', data);  // Debugging line
        setFullData(data); // Save the full data

        // If there's a timer, check if there's an existing countdown in localStorage
        if (data.timer) {
          const savedTime = localStorage.getItem('countdownTimer');
          if (savedTime && savedTime > 0) {
            setCountdownTimer(parseInt(savedTime)); // Load the saved timer from localStorage
          } else {
            setCountdownTimer(data.timer * 60); // Convert minutes to seconds
          }
        }

        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'alarm'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  // Countdown logic
  useEffect(() => {
    if (countdownTimer > 0) {
      const timerInterval = setInterval(() => {
        setCountdownTimer((prevTimer) => {
          const updatedTimer = prevTimer - 1;
          localStorage.setItem('countdownTimer', updatedTimer); // Save the timer in localStorage
          return updatedTimer;
        });
      }, 1000);

      return () => clearInterval(timerInterval); // Cleanup the interval when component unmounts
    } else if (countdownTimer === 0) {
      localStorage.removeItem('countdownTimer'); // Clear the countdown when it reaches zero
    }
  }, [countdownTimer]);

  const normalizeOnOffValue = (value) => {
    const normalized = value ? value.toLowerCase() : '';
    return normalized === 'on' || normalized === 'off' ? normalized.charAt(0).toUpperCase() + normalized.slice(1) : 'N/A';
  };

  const renderAlarmCard = (property, value) => {
    const normalizedOnOff = property === 'On/Off' ? normalizeOnOffValue(value) : '';
    const isOn = normalizedOnOff === 'On';
    const isActivated = property === 'Motion Detection' && value === 'activated';

    return (
      <div className="alarm-card">
        <div className="alarm-card-content">
          {(property === 'On/Off' || property === 'Motion Detection') && (
            <div className={`alarm-status-circle ${property === 'On/Off' ? (isOn ? 'on' : 'off') : (isActivated ? 'on' : 'off')}`}></div>
          )}
          {(property === 'Battery' && <img src={BatteryIcon} alt="Battery Icon" className="battery-icon" />) ||
            (property === 'Timer' && <img src={ClockIcon} alt="Clock Icon" className="clock-icon" />)}
          <div className="alarm-info">
            <div className="alarm-property">{property}</div>
            <div className="alarm-value">
              {property === 'Battery' ? value + '%' : property === 'Timer' ? formatTime(countdownTimer) : value}
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Format the countdown time as MM:SS
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds < 10 ? '0' : ''}${remainingSeconds}`;
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData ? (
        <div className="alarm-cards-container">
          {renderAlarmCard('On/Off', fullData.on_off || 'N/A')}
          {renderAlarmCard('Battery', fullData.battery || 'N/A')}
          {renderAlarmCard('Timer', fullData.timer || 'N/A')}
          {renderAlarmCard('Motion Detection', fullData.motion_detection || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alarm;
