import React, { useState, useEffect } from 'react';
import './Devices.css';

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.152:5000';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/alarm`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFullData(data);

        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'alarm');
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();

    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  const toggleAlarm = async () => {
    try {
      const response = await fetch(`${apiBaseUrl}/alarm/toggle`, { method: 'POST' });
      if (!response.ok) {
        throw new Error(`Failed to toggle alarm. Status: ${response.status}`);
      }
      const data = await response.json();
      setFullData(data);
    } catch (error) {
      setError('Error toggling alarm: ' + error.message);
    }
  };

  const updateBattery = async () => {
    try {
      const response = await fetch(`${apiBaseUrl}/alarm/update_battery`, { method: 'POST' });
      if (!response.ok) {
        throw new Error(`Failed to update battery. Status: ${response.status}`);
      }
      const data = await response.json();
      setFullData(data);
    } catch (error) {
      setError('Error updating battery: ' + error.message);
    }
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  const propertiesData = fullData ? [
    { property: 'on_off', value: fullData.on_off || 'N/A' },
    { property: 'battery', value: fullData.battery || 'N/A' },
    { property: 'timer', value: fullData.timer || 'N/A' },
    { property: 'motion_detection', value: fullData.motion_detection || 'N/A' }
  ] : [];

  return (
    <div className="text-center">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData ? (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Properties</h2>
            <div className="table-container">
              <table className="table-container1">
                <thead>
                  <tr className="row_0">
                    <th>Property</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {propertiesData.map((item, index) => (
                    <tr key={index}>
                      <td>{item.property}</td>
                      <td>{item.value}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div className="button-container">
              <button className="button" onClick={toggleAlarm}>
                Toggle Alarm
              </button>
              <button className="button" onClick={updateBattery}>
                Update Battery
              </button>
            </div>
          </div>

          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData && filteredData.map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}
