import React, { useState, useEffect } from 'react';
import './air_condition.css'; // Import the updated CSS file

export function AirCondition({ pageTitle }) {
  const [mainData, setMainData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.152:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch data from the /air_condition endpoint
        const response = await fetch(`${apiBaseUrl}/air_condition`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setMainData(data); // Save the main data

        // Fetch additional data from the /text endpoint for filtering
        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'air condition'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();

    // Optionally, set an interval to refresh the data
    const intervalId = setInterval(fetchData, 5000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  // Reverse data for bottom-to-top display
  const reversedFilteredData = filteredData ? [...filteredData].reverse() : [];

  return (
    <div className="page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {mainData && (
        <div className="tables-container">
          {/* Table for air condition properties */}
          <div className="table-container-wrapper">
            <h2>Air Condition Properties</h2>
            <div className="table-container">
              <table className="table-container1">
                <thead>
                  <tr className="row_0">
                    <th>Property</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(mainData).map(([key, value]) => (
                    <tr key={key}>
                      <td>{key}</td>
                      <td>{value}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Table for filtered air condition data */}
          <div className="table-container-wrapper">
            <h2>Air Condition Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {reversedFilteredData.map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
      {!mainData && <p>Loading data...</p>}
    </div>
  );
}
