import React, { useState, useEffect } from 'react';
import './air_condition.css'; // Import the updated CSS file
import HeatIcon from './hot.png'; // Adjust the path to where the image is stored
import SnowflakeIcon from './snowflake.png'; // Adjust the path to where the image is stored
import AutoIcon from './letter-a.png'; // Adjust the path to where the image is stored
import ClockIcon from './clock.png'; // Adjust the path to where the image is stored
import ThermometerIcon from './thermometer.png'; // Import the new thermometer icon

export function AirCondition({ pageTitle }) {
  const [mainData, setMainData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.15:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/air_condition`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Fetched Air Condition Data:', data);  // Debugging line
        setMainData(data); // Save the main data

        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'air condition'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  const formatTime = (time) => {
    return time ? `${time}'` : 'N/A';
  };

  const renderAirConditionCard = (property, value) => {
    let statusClass = '';
    let icon = null;

    if (property === 'On/Off') {
      statusClass = value.toLowerCase() === 'on' ? 'on' : 'off';
    }

    if (property === 'Mode' && value === 'heat') {
      icon = HeatIcon;
    } else if (property === 'Mode' && value === 'cold') {
      icon = SnowflakeIcon;
    } else if (property === 'Mode' && value === 'auto') {
      icon = AutoIcon;
    } else if (property === 'Timer') {
      icon = ClockIcon;
    } else if (property === 'Temperature') {
      icon = ThermometerIcon;
    }

    return (
      <div className={`air-condition-card ${statusClass}`}>
        <div className="air-condition-card-content">
          {statusClass && <div className={`air-condition-status-circle ${statusClass}`}></div>}
          {icon && <img src={icon} alt={`${property} Icon`} className="air-condition-icon" />}
          <div className="air-condition-info">
            <div className="air-condition-property">{property}</div>
            <div className="air-condition-value">
              {property === 'Temperature' ? `${value}Â°C` : value}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="air-condition-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {mainData ? (
        <div className="air-condition-cards-container">
          {renderAirConditionCard('On/Off', mainData.on_off || 'N/A')}
          {renderAirConditionCard('Temperature', mainData.temperature || 'N/A')}
          {renderAirConditionCard('Timer', mainData.timer || 'N/A')}
          {renderAirConditionCard('Mode', mainData.mode || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Air Condition Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default AirCondition;
