// Alarm.js
import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import CSS file

export function Alarm({ pageTitle }) {
  const [mainData, setMainData] = useState(null);
  const [additionalData, setAdditionalData] = useState(null);

  const apiBaseUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000';

  useEffect(() => {
    const fetchMainData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/alarm.json`); // Corrected URL
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const newData = await response.json();
        setMainData(newData);
      } catch (error) {
        console.error('Error fetching main data:', error);
      }
    };

    fetchMainData();
    const intervalId = setInterval(fetchMainData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  useEffect(() => {
    const fetchAdditionalData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/data.json`); // Corrected URL
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const allData = await response.json();
        const filteredData = allData.data.filter(item => item.device === 'alarm');
        setAdditionalData(filteredData);
      } catch (error) {
        console.error('Error fetching additional data:', error);
      }
    };

    fetchAdditionalData();
    const intervalId = setInterval(fetchAdditionalData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  return (
    <div className="Alarm text-center">
      <h1>{pageTitle}</h1>
      {mainData && additionalData ? (
        <div className="tables-container">
          <table className="table-container1">
            <thead>
              <tr>
                <th className="row_0">Property</th>
                <th className="row_0">Value</th>
              </tr>
            </thead>
            <tbody>
              {Object.entries(mainData).map(([key, value]) => (
                <tr key={key}>
                  <td>{key}</td>
                  <td>{value}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <table className="table-container2">
            <thead>
              <tr>
                <th>Original Text</th>
                <th>NLP Text</th>
                <th>Device</th>
                <th>Flag</th>
                <th>Time</th>
              </tr>
            </thead>
            <tbody>
              {additionalData.map((item, index) => (
                <tr key={index}>
                  <td>{item.original_text}</td>
                  <td>{item.nlp_text}</td>
                  <td>{item.device}</td>
                  <td>{item.flag}</td>
                  <td>{item.time}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}
