import React, { useState, useEffect } from 'react';
import './alarm.css'; // Import the updated CSS file
import BatteryIcon from './battery.png'; // Import the battery icon
import ClockIcon from './clock.png'; // Import the clock icon

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.1.7:5000'; // Update to your Flask server URL

  useEffect(() => {
    const eventSource = new EventSource(`${apiBaseUrl}/stream`); // SSE endpoint

    eventSource.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        console.log("Received fullData:", data); // Debugging line to see the structure of the data

        // If data is an array of alarm events
        if (Array.isArray(data)) {
          // Extract the latest alarm data (for simplicity, using the last item in the array)
          const latestAlarmData = data[data.length - 1];
          
          // Set the latest alarm data to fullData for card display
          setFullData(latestAlarmData);

          // Filter data to only include events related to 'alarm'
          const filtered = data.filter(item => item.device === 'alarm');
          setFilteredData(filtered);
        } else {
          console.error("Invalid data format:", data);
        }
      } catch (error) {
        setError('Error parsing event data: ' + error.message);
      }
    };

    eventSource.onerror = (error) => {
      setError('SSE connection error: ' + error.message);
    };

    return () => {
      eventSource.close();
    };
  }, [apiBaseUrl]);

  const normalizeOnOffValue = (value) => {
    const normalized = value ? value.toLowerCase() : '';
    return normalized === 'on' || normalized === 'off' ? normalized.charAt(0).toUpperCase() + normalized.slice(1) : 'N/A';
  };

  const renderAlarmCard = (property, value) => {
    const normalizedOnOff = property === 'On/Off' ? normalizeOnOffValue(value) : '';
    const isOn = normalizedOnOff === 'On';
    const isActivated = property === 'Motion Detection' && value === 'activated';

    return (
      <div className="alarm-card" key={property}>
        <div className="alarm-card-content">
          {/* Status circle for On/Off and Motion Detection */}
          {(property === 'On/Off' || property === 'Motion Detection') && (
            <div className={`alarm-status-circle ${property === 'On/Off' ? (isOn ? 'on' : 'off') : (isActivated ? 'on' : 'off')}`}></div>
          )}
          {/* Icons for Battery and Timer */}
          {property === 'Battery' && <img src={BatteryIcon} alt="Battery Icon" className="battery-icon" />}
          {property === 'Timer' && <img src={ClockIcon} alt="Clock Icon" className="clock-icon" />}
          
          {/* Property and value display */}
          <div className="alarm-info">
            <div className="alarm-property">{property}</div>
            <div className="alarm-value">
              {property === 'Battery' ? value + '%' : property === 'Timer' ? formatTime(value) : value}
            </div>
          </div>
        </div>
      </div>
    );
  };

  const formatTime = (time) => {
    return time ? `${time}'` : 'N/A';
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      
      {/* Render the alarm cards if fullData is available */}
      {fullData ? (
        <div className="alarm-cards-container">
          {renderAlarmCard('On/Off', fullData.nlp_text || 'N/A')}
          {renderAlarmCard('Battery', 'N/A')} {/* Adjust based on your actual data */}
          {renderAlarmCard('Timer', 'N/A')} {/* Adjust based on your actual data */}
          {renderAlarmCard('Motion Detection', 'N/A')} {/* Adjust based on your actual data */}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {/* Render the filtered data table */}
      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alarm;
