import React, { useState, useEffect } from 'react';
import './Devices.css'; // Import the updated CSS file

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);

  const apiBaseUrl = 'http://192.168.2.12:5000'; // Update to your Flask server URL

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/text`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFullData(data); // Save the full data
        const filtered = data.data.filter(item => item.device === 'alarm'); // Filter data by device
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();

    // Optionally, set an interval to refresh the data
    const intervalId = setInterval(fetchData, 5000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="text-center">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData && (
        <div className="tables-container">
          {/* Table for full data */}
          <div className="table-container-wrapper">
            <h2>Full Data History</h2>
            <table className="table-container1">
              <thead>
                <tr className="row_0">
                  <th>Original Text</th>
                  <th>NLP Text</th>
                  <th>Device</th>
                  <th>Flag</th>
                  <th>Time</th>
                </tr>
              </thead>
              <tbody>
                {fullData.data.map((item, index) => (
                  <tr key={index}>
                    <td>{item.original_text}</td>
                    <td>{item.nlp_text}</td>
                    <td>{item.device}</td>
                    <td>{item.flag}</td>
                    <td>{item.time}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Table for filtered alarm data */}
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <table className="table-container2">
              <thead>
                <tr className="row_0">
                  <th>Original Text</th>
                  <th>NLP Text</th>
                  <th>Device</th>
                  <th>Flag</th>
                  <th>Time</th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((item, index) => (
                  <tr key={index}>
                    <td>{item.original_text}</td>
                    <td>{item.nlp_text}</td>
                    <td>{item.device}</td>
                    <td>{item.flag}</td>
                    <td>{item.time}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
      {!fullData && <p>Loading data...</p>}
    </div>
  );
}
