import React, { useState, useEffect } from 'react';
import './alarm.css';
import BatteryIcon from './battery.png';
import ClockIcon from './clock.png';

export function Alarm({ pageTitle }) {
  const [fullData, setFullData] = useState(null);
  const [filteredData, setFilteredData] = useState(null);
  const [error, setError] = useState(null);
  const [countdownTimer, setCountdownTimer] = useState(null);

  const apiBaseUrl = 'http://192.168.1.7:5000';

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`${apiBaseUrl}/alarm`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFullData(data);

        // Set initial countdown timer
        setCountdownTimer(data.timer * 60); // Convert minutes to seconds

        const additionalResponse = await fetch(`${apiBaseUrl}/text`);
        if (!additionalResponse.ok) {
          throw new Error(`HTTP error! Status: ${additionalResponse.status}`);
        }
        const additionalData = await additionalResponse.json();
        const filtered = additionalData.data.filter(item => item.device === 'alarm');
        setFilteredData(filtered);
      } catch (error) {
        setError('Error fetching data: ' + error.message);
      }
    };

    fetchData();
    const intervalId = setInterval(fetchData, 5000);

    return () => clearInterval(intervalId);
  }, [apiBaseUrl]);

  useEffect(() => {
    if (countdownTimer <= 0) return;

    const timerId = setInterval(() => {
      setCountdownTimer(prevTime => prevTime - 1);
    }, 1000);

    return () => clearInterval(timerId);
  }, [countdownTimer]);

  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
  };

  if (error) {
    return <p className="text-center" style={{ color: 'red' }}>{error}</p>;
  }

  return (
    <div className="alarm-page-container">
      <div className="header">
        <h1>{pageTitle}</h1>
        <hr className="line-under-header" />
      </div>
      {fullData ? (
        <div className="alarm-cards-container">
          {renderAlarmCard('On/Off', fullData.on_off || 'N/A')}
          {renderAlarmCard('Battery', fullData.battery || 'N/A')}
          {renderAlarmCard('Timer', formatTime(countdownTimer) || 'N/A')}
          {renderAlarmCard('Motion Detection', fullData.motion_detection || 'N/A')}
        </div>
      ) : (
        <p>Loading data...</p>
      )}

      {filteredData && (
        <div className="tables-container">
          <div className="table-container-wrapper">
            <h2>Alarm Information</h2>
            <div className="table-container">
              <table className="table-container2">
                <thead>
                  <tr className="row_0">
                    <th>Original Text</th>
                    <th>NLP Text</th>
                    <th>Device</th>
                    <th>Flag</th>
                    <th>Time</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredData.slice().reverse().map((item, index) => (
                    <tr key={index}>
                      <td>{item.original_text}</td>
                      <td>{item.nlp_text}</td>
                      <td>{item.device}</td>
                      <td>{item.flag}</td>
                      <td>{item.time}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Alarm;
